package basicprograms.JavaBasics;

public class DeclareGlobalVariable {

	
	int a =10; //global variable
	
	int b =23; //global variable
	
	boolean truefalse = true;  //global variable
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		DeclareGlobalVariable dgv = new DeclareGlobalVariable();
		
		System.out.println("Value of a : " +dgv.a );
		
		System.out.println("Valu of b : " +dgv.b);
		
		System.out.println(dgv.truefalse);
		
	}

}
