package basicprograms;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int a =10;
		int b =2;
		
		// Addition Operator
		System.out.println(a+b);
		// subtraction Operator
		System.out.println(a-b);
		// Divison Operator
		System.out.println(a/b);
		//Modulus Operator
		System.out.println(a%b);
		// Multiplication Operator
		System.out.println(a*b);
	}

}
